mapper.select:
SELECT

FROM farmer_product
WHERE is_deleted = 0
AND farmer_id = #{farmerId}

	AND status = #{status}

ORDER BY created_at DESC

SELECT

FROM farmer_product
WHERE id = #{id}
AND farmer_id = #{farmerId}
AND is_deleted = 0

mapper.update:
UPDATE farmer_product
SET is_deleted = 1,
	updated_at = NOW()
WHERE id = #{id}
  AND farmer_id = #{farmerId}
  AND is_deleted = 0

UPDATE farmer_product
SET status = #{status},
	updated_at = NOW()
WHERE id = #{id}
  AND farmer_id = #{farmerId}
  AND is_deleted = 0
